import CatalogItemsActions from '../actions/catalogItemsActions';

const initialState = [];

function catalogItemsReducer(state = initialState, action) {
  switch (action.type) {
    case CatalogItemsActions.ADD:
      return [...state, action.payload.task];

    case CatalogItemsActions.DELETE:
      return [
        state.filter((item) => item.id !== action.payload.itemId),
      ];

    default:
      return state;
  }
}

export default catalogItemsReducer;
