import { connect } from 'react-redux';
import {
  INCREASE, DECREASE, DELETE, DELETEALL,
} from '../../../redux/actions/catalogItemsActions';
import BusketTable from './BasketTableComponent';

const ConnectedBusketTable = connect(
  (state) => ({
    items: state,
  }),
  (dispatch) => ({
    onIncrease: (itemId, counter) => dispatch({
      type: INCREASE.type,
      payload: { itemId, counter },
    }),
    onDecrease: (itemId, counter) => dispatch({
      type: DECREASE.type,
      payload: { itemId, counter },
    }),
    OnDelete: (itemId) => dispatch({
      type: DELETE.type,
      payload: { itemId },
    }),
    OnDeleteAll: (items) => dispatch({
      type: DELETEALL.type,
      payload: { items },
    }),
  }),
)(BusketTable);

export default ConnectedBusketTable;
