import React from 'react';
import ConnectedHeader from '../../main/header/container/HeaderContainer';
import Footer from '../../main/footer/components/FooterComponent';
import PopUpErrorLoading from '../../../common/popup/components/PopUpErrorLoadingComponent';
import postHistoryOrder from '../api/post/postHistoryOrder';

class OrdersHistory extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: true,
      errorMessage: '',
      popupSmthWentWrongActive: true,
      ordersArray: [],
    };
  }

  async componentDidMount() {
    const { pages } = this.props;
    await postHistoryOrder(pages.loginPersonalAccountReducer.id, this.updateData);
  }

  setpopupSmthWentWrongActive = (value) => { this.setState({ popupSmthWentWrongActive: value }); }

  updateData = (name, value) => {
    if (name === 'ordersArray') this.setState({ ordersArray: value });
    if (name === 'isLoading') this.setState({ isLoading: value });
    if (name === 'error') this.setState({ errorMessage: value });
  }

  render() {
    const {
      isLoading, errorMessage, popupSmthWentWrongActive, ordersArray,
    } = this.state;
    console.log(ordersArray);
    if (isLoading) {
      return <div className="-isLoading"> </div>;
    }
    if (errorMessage) {
      return (
        <PopUpErrorLoading
          popupSmthWentWrongActive={popupSmthWentWrongActive}
          setpopupSmthWentWrongActive={this.setpopupSmthWentWrongActive}
        />
      );
    }
    return (
      <>
        <ConnectedHeader linkItem={<button type="button" className="buttonBack">Главная</button>} link="/main-page" disabled />
        <div className="ordersHistory-box">
          <h2 className="orders"> История заказов</h2>
          <div className="title">
            <p className="titleGood">Товар</p>
            <p className="titlePrice">Цена</p>
            <p className="titleQuantity">Количество</p>
            <p className="titleAmount">Сумма</p>
            <p className="titleDelete"> </p>
          </div>
          <ul>
            <div className="columns">
              { ordersArray.map((order) => (
                <li key={order.id}>
                  <div className="columnGood">
                    {/*   {order.image} */}
                    <h4 className="columnGoodNameTitle">
                      {order.description}
                    </h4>
                  </div>
                  <p className="columnPrice">
                    {order.price}
                    <br />
                    <span className="priceLetters">руб. за шт</span>
                  </p>
                  <div className="columnQuantity">
                    {order.counter}
                  </div>
                  <p className="columnAmount">
                    {order.sum}
                    руб.
                  </p>
                </li>
              ))}
            </div>
          </ul>
        </div>
        <Footer />
      </>
    );
  }
}

export default OrdersHistory;
