import axios from 'axios';
import serverUrl from '../../../../common/constants/urls';

const ordersHistory = '/orders-history';

async function postHistoryOrder(id, updateData, setState) {
  const payload = {
    data: {
      id,
    },
  };

  try {
    const response = await axios.post(`${serverUrl}${ordersHistory}`, payload);
    const result = response.data;

    updateData('ordersArray', result, setState);
    updateData('isLoading', false, setState);
    return result;
  } catch (error) {
    updateData('error', error.message, setState);
    return null;
  }
}

export default postHistoryOrder;
