/* eslint-disable react/destructuring-assignment */
import React from 'react';
import postCategoryName from '../api/post/postCategoryName';
import getCategoryName from '../api/get/getCategoryName';

class InputEditCategoryName extends React.Component {
  constructor(props) {
    super(props);
    const { category, id } = this.props;
    this.state = {
      id,
      categoryName: category,
    };
    this.updateData = this.updateData.bind(this);
  }

  async handleImageChange(e) {
    e.preventDefault();

    this.setState({
      categoryName: e.target.value,
    });
    postCategoryName(this.state.id, e.target.value);
  }

  updateData(value) {
    this.setState({
      categoryName: value,
    });
  }

  render() {
    const { categoryName } = this.state;
    return (
      <>
        <input
          type="text"
          value={categoryName}
          onChange={async (e) => {
            this.handleImageChange(e);
          }}
        />
      </>
    );
  }
}

export default InputEditCategoryName;
