import React from 'react';
import { connect } from 'react-redux';
import personalAccount from '../../../assets/personal-account/personal-account.jpg';
import { ADDPHOTO } from '../../../redux/actions/loginPersonalAccountActions';

class ImageUpload extends React.Component {
  onAddPhoto = this.props;

  imagePreviewUrl = this.state;

  constructor(props) {
    super(props);
    const { pages } = this.props;
    this.state = {
      imagePreviewUrl: pages.loginPersonalAccountReducer.photo,
    };
  }

  handleSubmit(e) {
    this.e.preventDefault();
  }

  handleImageChange(e) {
    e.preventDefault();

    const reader = new FileReader();
    const file = e.target.files[0];

    if (file) {
      reader.onloadend = () => {
        this.setState({
          imagePreviewUrl: reader.result,
        });
        this.onAddPhoto(this.imagePreviewUrl);
      };

      reader.readAsDataURL(file);
    }
  }

  render() {
    const { imagePreviewUrl } = this.state;
    let $imagePreview = null;
    if (imagePreviewUrl) {
      $imagePreview = (
        <img
          src={imagePreviewUrl}
          className="photoPersonalAccount"
          title="photoPersonalAccount"
          alt="photoPersonalAccount"
        />
      );
    } else {
      $imagePreview = (
        <img
          src={personalAccount}
          title="photoPersonalAccount"
          alt="photoPersonalAccount"
          className="photoPersonalAccount"
        />
      );
    }
    return (
      <div className="previewComponent">
        <form onSubmit={(e) => this.handleSubmit(e)} id="upload-container">
          {$imagePreview}
          <div className="imgPreview">
            <label htmlFor="avatar">
              <p
                className="choosePhotoButton"
              >
                Выберите фото
              </p>
              <input
                id="avatar"
                name="avatar"
                className="avatar"
                type="file"
                onChange={(e) => this.handleImageChange(e)}
                multiple
              />
            </label>
          </div>
        </form>
      </div>
    );
  }
}

const ConnectedImageUpload = connect(
  (state) => ({
    pages: state,
  }),
  (dispatch) => ({
    onAddPhoto: (photo) => dispatch({
      type: ADDPHOTO.type,
      payload: { photo },
    }),
  }),
)(ImageUpload);

export default ConnectedImageUpload;
