import { ADD, DECREASE, INCREASE } from '../actions/catalogItemsActions';

const initialState = [];

export default (state = initialState, action) => {
  switch (action.type) {
    case ADD.type:
      return [...state, action.payload.item];

    case CatalogItemsActions.type:
      return [...state, action.payload.item.counter + 1];

      case COUNTER.type:
        return [...state, action.payload.item.counter + 1];

    default:
      return state;
  }
};
