import axios from 'axios';
import serverUrl from '../../../../common/constants/urls';

const catalogImage = '/category-image';

async function postUserPhoto(id, image) {
  const errorPhoto = document.getElementById('errorPhoto');
  const payload = {
    data: {
      id,
      image,
    },
  };

  try {
    const response = await axios.post(`${serverUrl}${catalogImage}`, {
      payload,
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    const result = response.data;
    errorPhoto.setAttribute('class', 'errorPhoto -disabled');
    return result;
  } catch (error) {
    errorPhoto.setAttribute('class', 'errorPhoto');
    return error.message;
  }
}

export default postUserPhoto;
