const initialState = {
  items: [],
};

export default (state = initialState, action) => {
  switch (action.type) {
    case 'set':
      return {
        ...state,
        items: action.payload,
      };

    case 'add':
      return {
        ...state,
        items: [...state.items, action.payload],
      };

    default:
      return state;
  }
};
