import axios from 'axios';
import serverUrl from '../../../../common/constants/urls';

const productsPrice = '/products-price';

async function postProductsPrice(id, price, setIsProductsUpdated, isProductsUpdated) {
  const payload = {
    data: {
      id,
      price,
    },
  };

  try {
    const response = await axios.post(`${serverUrl}${productsPrice}`, payload);
    const result = response.data;
    isProductsUpdated
      ? setIsProductsUpdated(false)
      : setIsProductsUpdated(true);
    console.log(isProductsUpdated);
    return result;
  } catch (error) {
    return null;
  }
}

export default postProductsPrice;
