import React from 'react';

const ButtonEditCategory = ({
  className = '', category, handleButtonOnDelete = '', handleButtonOnSave = '', handleButtonOnEdit = '',
  IsEditButtonVisible = false, isEditActive = false, isEditActiveId = false,
}) => (
  <button
    type="button"
    className={className}
    onClick={() => {
      (IsEditButtonVisible && isEditActive && category.id === isEditActiveId)
        ? handleButtonOnSave(category) : handleButtonOnEdit(category);
      if (handleButtonOnDelete !== '') { handleButtonOnDelete(); }
    }}
  >
    {(IsEditButtonVisible && isEditActive && category.id === isEditActiveId && handleButtonOnDelete === '') ? 'Сохранить' : 'Изменить'}
    {(handleButtonOnDelete !== '' && handleButtonOnEdit === '') ? 'Удалить' : ''}
  </button>
);
export default ButtonEditCategory;
