import React from 'react';

function formatPhoneNumber(stringPhone) {
  const stringPhoneWithoutPlus = stringPhone.slice(1);
  console.log(stringPhoneWithoutPlus, typeof stringPhoneWithoutPlus);

  let numberPhone = '';
  let letterNumb = '';
  const stringNumber = stringPhoneWithoutPlus.filter((letter) => '0123456789'.includes(letterNumb));

  // eslint-disable-next-line no-restricted-syntax
  for (letterNumb of stringPhoneWithoutPlus) {
    if ('0123456789'.includes(letterNumb)) {
      numberPhone += letterNumb;
    }
  }

  return (
    `+375${letterNumb}`
  );
}

export default formatPhoneNumber;
