import React from 'react';
import ConnectedHeader from '../../main/header/container/HeaderContainer';
import Footer from '../../main/footer/components/FooterComponent';
import AddCategoryProductImage from '../../editCategory/components/AddCategoryProductImage';
import PopUpErrorLoading from '../../../common/popup/components/PopUpErrorLoadingComponent';
import handleButtonClick from '../utils/handleButtonClick';
import getCategories from '../../editCategory/api/get/getCategories';
import checkOnlyNumbers from '../../../common/untils/checkOnlyNumbers';
import setClassErrorById from '../../../common/untils/setClassErrorById';

class AddProductPage extends React.Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.state = {
      productName: '',
      image: '',
      categoryName: 'Электротовары и свет',
      productPrice: '',
      productInStock: 'да',
      categoriesArray: [],
      errorMessage: '',
      isLoading: false,
      popupSmthWentWrongActive: true,
      isPriceCorrect: false,
    };
  }

  async componentDidMount() {
    this._isMounted = true;
    await getCategories(this.updateDataCategories, this.setError);
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  setError = (value) => { this.setState({ errorMessage: value }); }

  setpopupSmthWentWrongActive = (value) => { this.setState({ popupSmthWentWrongActive: value }); }

  updateDataCategories = (value, valueIsLoading) => {
    if (this._isMounted) {
      this.setState({
        categoriesArray: value,
        isLoading: valueIsLoading,
      });
    }
  }

  updateData = (name, e, state = '') => {
    if (name === 'categoryName') this.setState({ categoryName: e.target.value });
    if (name === 'productName') this.setState({ productName: e.target.value });
    if (name === 'productPrice') this.setState({ productPrice: e.target.value });
    if (name === 'productInStock') this.setState({ productInStock: e.target.value });
    if (name === 'onBlur') this.setState({ productPrice: `${e.target.value} РУБ.` });
    if (name === 'onBlur') this.setState({ isPriceCorrect: checkOnlyNumbers(state.productPrice) });
  }

  updateImage = (value) => { this.setState({ image: value }); }

  render() {
    const {
      productName, categoryName, productPrice, categoriesArray,
      errorMessage, isLoading, popupSmthWentWrongActive,
      isPriceCorrect,
    } = this.state;
    const {
      history, isProductsUpdated, setIsProductsUpdated, pages,
    } = this.props;

    if (!isLoading) {
      return <div className="-isLoading"> </div>;
    }
    if (errorMessage) {
      return (
        <PopUpErrorLoading
          popupSmthWentWrongActive={popupSmthWentWrongActive}
          setpopupSmthWentWrongActive={this.setpopupSmthWentWrongActive}
        />
      );
    }

    return (
      <>
        <ConnectedHeader linkItem={<button type="button" className="buttonBack">Назад</button>} link="/personal" disabled={false} />
        {pages.loginPersonalAccountReducer.personAccountIsVisible ? (
          <div className="addProduct-box">
            <h2 className="">Добавить товар</h2>
            <div className="addProduct-container">
              <p className="productCategoriesString">Категория</p>
              <select
                className="productsCategories"
                value={categoryName}
                onChange={(event) => this.setState({ categoryName: event.target.value })}
              >
                {categoriesArray.categories.map((category) => (
                  <option
                    key={category.id}
                    value={category.category}
                  >
                    {category.category}
                  </option>
                ))}
              </select>
              <p className="productNameString">Название</p>
              <input
                type="text"
                className="productNameInput"
                value={productName}
                onChange={(e) => this.setState({ productName: e.target.value })}
              />
              <p className="productPriceString">Цена</p>
              <p id="errorPrice" className="errorPrice -disabled">Цена должна содержать только цифры</p>
              <input
                type="text"
                className="productPriceInput"
                value={productPrice}
                onChange={(e) => {
                  this.setState({
                    productPrice: e.target.value,

                  });
                }}
                onBlur={(e) => {
                  this.setState({
                    productPrice: `${e.target.value} РУБ.`,
                    isPriceCorrect: checkOnlyNumbers(productPrice),
                  });
                }}
              />
              <p className="productInStockString">В наличии</p>
              <select
                className="imageColors"
                onChange={(event) => this.setState({ productInStock: event.target.value })}
              >
                <option value defaultValue>да</option>
                <option value={false}>нет</option>
              </select>
              <AddCategoryProductImage updateImage={this.updateImage} />
              <button
                className="addNewCategoryButton"
                type="button"
                onClick={() => (isPriceCorrect
                  ? handleButtonClick(this.state, history, isProductsUpdated, setIsProductsUpdated)
                  : setClassErrorById('errorPrice', 'errorPrice'))}
              >
                Добавить
              </button>
            </div>
          </div>
        ) : null}
        <Footer />
      </>
    );
  }
}

export default AddProductPage;
