const initialState = {
  items: [{
    id: 0,
    desciption: 'Светильник потолочный Box Silver',
  }],
  ids: [],
};

const actions = {
  SET_ITEM: 'set',
  ADD_ITEM: 'add',
  DELETE_ITEM: 'delete',
};

export default (state = initialState, action) => {
  switch (action.type) {
    case actions.SET_ITEM:
      return {
        ...state,
        items: actions.payload,
      };

    case actions.ADD_ITEM:
      return {
        ...state,
        items: [...state.items, action.payload],
      };

    default:
      return state;
  }
};
