import React from 'react';

const ButtonEditCategory = ({
  className = '', category, handleButtonOnSave = '', handleButtonOnEdit = '', handleButtonOnDelete = '',
  IsEditButtonVisible = '', isEditActive = '', isEditActiveId = '',
}) => (
  <button
    type="button"
    className={className}
    onClick={() => {
      if (handleButtonOnSave !== '') { handleButtonOnSave(category); }
      if (handleButtonOnEdit !== '') { handleButtonOnEdit(category); }
      if (handleButtonOnDelete !== '') { handleButtonOnDelete(); }
    }}
  >
    {(IsEditButtonVisible && isEditActive && category.id === isEditActiveId) ? 'Сохранить' : 'Изменить'}

  </button>
);
export default ButtonEditCategory;
