function formatPhoneNumber(value) {
  const phone = [];
  let phones = [];
  console.log(value);
  value.split('').forEach((letter, index) => {
    if ('+0123456789'.includes(letter) && index === 4) {
      console.log(index);
    }
    if ('+0123456789'.includes(letter)) {
      phone.push(letter);
      phones = phone.join(',');
      console.log(phones, value);
    }
  });
  return phones;
}

export default formatPhoneNumber;

