/* eslint-disable react/destructuring-assignment */
import React from 'react';
import postCategoryName from '../api/post/postCategoryName';
import getCategoryName from '../api/get/getCategoryName';

class InputEditCategoryName extends React.Component {
  constructor(props) {
    super(props);
    const { category, id } = this.props;
    this.state = {
      id,
      categoryName: category,
    };
    this.updateData = this.updateData.bind(this);
  }

  async componentDidMount() {
    await getCategoryName(this.state.id);
    console.log('test');
  }

  updateData(value) {
    this.setState({
      categoryName: value,
    });
  }

  render() {
    const { categoryName, id } = this.state;
    const { setEditActive } = this.props;
    return (
      <>
        <input
          type="text"
          value={categoryName}
          onChange={(e) => {
            this.setState({ categoryName: e.target.value });
          }}
          onBlur={async () => {
            setEditActive(false);
            await postCategoryName(id, categoryName, this.updateData);
          }}
        />
      </>
    );
  }
}

export default InputEditCategoryName;
