import React from 'react';

class LoginFormForgetPassword extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      clientEmail: '',
    };
  }

  render() {
    const {
      clientEmail,
    } = this.state;

    const {
      hideLoginPage, showForgetPasswordPage,
    } = this.props;

    return (
      <form className="form">
        <div className="login">
          <p className="loginString">Email:</p>
          <input
            className="loginInput"
            type="text"
            name="LOGIN"
            value={clientEmail}
            onChange={((event) => this.setState({ clientEmail: event.target.value }))}
          />
        </div>
        <button
          type="button"
          className="entranceButton"
        >
          Отправить
        </button>
        <button
          type="button"
          className="forgotPasswordButton"
          onClick={() => {
          }}
        >
          Войти
        </button>
        <button
          type="button"
          className="registrationButton"
        >
          Регистрация
        </button>
      </form>
    );
  }
}

export default LoginFormForgetPassword;
