import getRequest from '../../../../common/api/get/getRequest';

const categoryPath = '/category';

async function getCategories(updateData, isMounted, isCategory, setState) {
  try {
    const response = await getRequest(categoryPath);
    const result = response.data;
    updateData(isMounted, isCategory, setState, { categories: result }, true);
    return result;
  } catch (error) {
    return null;
  }
}

export default getCategories;
