/* eslint-disable react/destructuring-assignment */
import React from 'react';
import { connect } from 'react-redux';
import { LOGIN } from '../../../redux/actions/loginPersonalAccountActions';
import ConnectedLogin from './LoginComponent';

class LoginForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isVisible: true,
    };
  }

  componentDidMount() {
    document.addEventListener('click', this.onClick, false);
  }

  componentWillUnmount() {
    document.removeEventListener('click', this.onClick, false);
  }

   onClick = (event) => {
     if (this.props.divRef.current.contains(event.target)) {
       this.setState({ isVisible: true });
     } else {
       this.setState({ isVisible: false });
       this.props.onLogin(false, false, false);
     }
   };

   render() {
     const {
       isVisible,
     } = this.state;

     if (!isVisible) {
       return null;
     }
     return (
       <div
         className="loginForm"
         onClick={(e) => {
           e.stopPropagation();
           e.preventDefault();
         }}
         role="presentation"
       >
         <h2>Личный кабинет</h2>
         <div
           onClick={() => {
             this.setState({ isVisible: false });
           }}
           role="presentation"
         >

         </div>
         <ConnectedLogin />
       </div>
     );
   }
}

const ConnectedLoginForm = connect(
  (state) => ({
    pages: state,
  }),
  (dispatch) => ({
    onLogin: (
      loginFormIsVisible, loginFormLoginPageIsVisible, loginFormForgetPasswordIsVisible,
    ) => dispatch({
      type: LOGIN.type,
      payload: {
        loginFormIsVisible, loginFormLoginPageIsVisible, loginFormForgetPasswordIsVisible,
      },
    }),
  }),
)(LoginForm);

export default ConnectedLoginForm;
