import React from 'react';
import ErrorSymbol from '../../../common/components/icons/components/ErrorSymbolComponent';
import removeErrorNotNull from '../../../common/utils/removeErrorNotNull';
import formatPhoneNumber from '../../../common/utils/formatPhoneNumber';

class InputPopUpBasketOrder extends React.Component {
  constructor(props) {
    super(props);
    const { pages } = this.props;
    this.state = {
      clientName: '' || pages.loginPersonalAccountReducer.firstName,
      clientPhone: pages.loginPersonalAccountReducer.phone ? pages.loginPersonalAccountReducer.phone : '+375(__)___-__-__',
      clientAddress: '' || pages.loginPersonalAccountReducer.address,
    };
  }

  updateData = (value, name) => {
    if (name === 'clientName') { this.setState({ clientName: value }); }
    if (name === 'clientAddress') { this.setState({ clientAddress: value }); }
    if (name === 'phone') { this.setState({ clientPhone: formatPhoneNumber(value) }); }
  };

  updatePhone = () => this.setState({ clientPhone: '+375' });

  render() {
    const { clientName, clientPhone, clientAddress } = this.state;
    return (
      <>
        <div className="name">
          <p className="nameString -required">Имя</p>
          <input
            value={clientName}
            type="text"
            name="clientName"
            classInput="nameInput"
            classSymbol="nameSymbol"
            updateData={this.updateData}
            removeErrorNotNull={removeErrorNotNull}
          />
          <ErrorSymbol Class="nameSymbol -disabled" />
        </div>
        <>
          <input
            value={clientPhone}
            type="tel"
            name="phone"
            classInput="phoneInput"
            classSymbol="phoneSymbol"
            updateData={this.updateData}
            removeErrorNotNull={removeErrorNotNull}
            minLength="13"
            maxLength="13"
            placeholder="+375 (__) ___-__-__"
            updatePhone={this.updatePhone}
          />
          <ErrorSymbol Class="phoneSymbol -disabled" />
        </>
        <>
          <input
            initialValue={clientAddress}
            type="text"
            name="clientAddress"
            classInput="addressInput"
            classSymbol="addressSymbol"
            updateData={this.updateData}
            removeErrorNotNull={removeErrorNotNull}
          />
          <ErrorSymbol Class="addressSymbol -disabled" />
        </>
      </>
    );
  }
}

export default InputPopUpBasketOrder;
