import React from 'react';
import DragAndDrop from './DragAndDropComponent';
import personalAccount from '../../../assets/personal-account/personal-account.jpg';

class Photo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      /*      src: personalAccount, */
      files: [],
    };
  }

  /*   handleDrop = (photo) => {
    this.setState({ file: photo });
  } */

  handleDrop = (files) => {
    const fileList = files;
    fileList.push(files.name);
    this.setState({ files: fileList });
  }

  render() {
    const { files } = this.state;
    return (
      <DragAndDrop handleDrop={this.handleDrop}>
        {/*         <img
          className="photoPersonalAccount"
          src={src}
          alt="Фото"
          title="Фото"
        /> */}
        <div style={{ height: 300, width: 250 }}>
          {files.map((file) => <div>{file}</div>)}
        </div>
      </DragAndDrop>
    );
  }
}

export default Photo;
