/* eslint-disable no-unused-vars */
// eslint-disable-next-line no-unused-vars
import React from 'react';

function formatPhoneNumber(stringPhone) {
  const stringPhoneWithoutPlus = stringPhone.slice(1);
  console.log(stringPhoneWithoutPlus, typeof stringPhoneWithoutPlus);

  const stringNumber = stringPhoneWithoutPlus.forEach((letter) => '0123456789'.includes(letter));
  return (
    `+375${stringNumber}`
  );
}

export default formatPhoneNumber;
