// eslint-disable-next-line no-unused-vars
import React from 'react';

function formatPhoneNumber(value) {
  let phone = '';
  value.split('').forEach((letter) => {
    if ('+0123456789'.includes(letter)) {
      phone += letter;
    }
  });
  return phone;
}

export default formatPhoneNumber;
