import React from 'react';
import addRemoveScroll from '../../../utils/addRemoveScroll';

const ButtonPopupBasket = ({
  className, closePopup, runAddRemoveScroll, children,
}) => (
  <button
    type="button"
    className={className}
    onClick={() => {
      runAddRemoveScroll ? closePopup(false) && addRemoveScroll() : closePopup(false);
    }}
  >
    {children}
  </button>
);
export default ButtonPopupBasket;
