import React from 'react';
import { Link } from 'react-router-dom';
import setClassNotNull from '../../../common/untils/setClassNotNull';

class ForgetPassword extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      clientEmail: '',
    };
  }

  render() {
    const {
      clientEmail,
    } = this.state;
    const { setLoginFormLoginPage, setLoginLoginFormForgetPassword } = this.props;
    return (
      <form className="form">
        <div className="login">
          <p className="loginString">Email:</p>
          <input
            className="loginInput"
            type="text"
            name="LOGIN"
            value={clientEmail}
            onChange={((event) => this.setState({ clientEmail: event.target.value }))}
          />
        </div>

        <div
          type="button"
          className=""
        >
          {clientEmail ? (
            <Link to="/change-password">
              <button
                type="button"
                className="entranceButton"
              >
                Отправить
              </button>
            </Link>
          ) : (
            <button
              type="button"
              className="entranceButton"
              onClick={() => setClassNotNull()}
            >
              Отправить
            </button>
          )}
        </div>

        <button
          type="button"
          className="forgotPasswordButton"
          onClick={() => {
            setLoginFormLoginPage(true);
            setLoginLoginFormForgetPassword(false);
          }}
        >
          Войти
        </button>
        <button
          type="button"
          className="registrationButton"
        >
          Регистрация
        </button>
      </form>
    );
  }
}

export default ForgetPassword;
