/* eslint-disable prefer-destructuring */
import React from 'react';
import axios from 'axios';

class FileUpload extends React.Component {
  constructor(props) {
    super(props);
    this.state = { val: 0 };
  }

  componentDidMount() {
    const { val } = this.state;
    setInterval(() => {
      fetch('http://localhost:8080/image').then((res) => res.json())
        .then((res) => {
          this.setState(
            { val: res.value },
          );
        });
    }, 1000);
  }

  render() {
    const { val } = this.state;
    return (
      <>
        {val}
      </>
    );
  }
}

export default FileUpload;
