import { ADD, INCREASE, DECREASE } from '../actions/catalogItemsActions';

const initialState = [];

export default (state = initialState, action) => {
  switch (action.type) {
    case ADD.type:
      return [...state, action.payload.item];

    case INCREASE.type: {
      const index = state.findIndex((item) => item.id === action.payload.itemId);
      return [
        ...state.slice(0, index),
        { ...state[index], counter: action.payload.counter + 1 },
        ...state.slice(index + 1),
      ];
    }

    case DECREASE.type: {
      if (action.payload.counter > 0) {
        const index = state.findIndex((item) => item.id === action.payload.itemId);
        return [
          ...state.slice(0, index),
          { ...state[index], counter: action.payload.counter - 1 },
          ...state.slice(index + 1),
        ];
      } return state;
    }

    default:
      return state;
  }
};
