import React from 'react';
import { Link } from 'react-router-dom';
import setClassNotNull from '../../untils/setClassNotNull';

class ForgetPassword extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      clientEmail: '',
    };
  }

  render() {
    const {
      clientEmail,
    } = this.state;
    const { setLoginFormLoginPage, setLoginLoginFormForgetPassword } = this.props;
    return (
      <form className="form">
        <div className="login">
          <p className="loginString">Email:</p>
          <input
            className="emailInput"
            id="emailInput"
            type="email"
            name="EMAIL"
            value={clientEmail}
            onChange={((event) => this.setState({ clientEmail: event.target.value }))}
          />
        </div>

        <svg className="errorSymbol" viewBox="0 0 20 20">
          <g>
            <path d="M17.89,0H2.11A2.11,2.11,0,0,0,0,2.11V17.89A2.11,2.11,0,0,0,2.11,20H17.89A2.11,2.11,0,0,0,20,17.89V2.11A2.11,2.11,0,0,0,17.89,0ZM19,17.89A1.11,1.11,0,0,1,17.89,19H2.11A1.11,1.11,0,0,1,1,17.89V2.11A1.11,1.11,0,0,1,2.11,1H17.89A1.11,1.11,0,0,1,19,2.11V17.89Z" />
            <path d="M13,7a.5.5,0,0,0-.71,0L10,9.29,7.68,7A.5.5,0,0,0,7,7.68L9.29,10,7,12.32a.5.5,0,0,0,.71.71L10,10.71,12.32,13a.5.5,0,0,0,.71-.71L10.71,10,13,7.68A.5.5,0,0,0,13,7Z" />
          </g>
        </svg>

        <div
          type="button"
          className=""
        >
          {clientEmail ? (
            <Link to="/change-password">
              <button
                type="button"
                className="entranceButton"
              >
                Отправить
              </button>
            </Link>
          ) : (
            <button
              type="button"
              className="entranceButton"
              onClick={() => setClassNotNull()}
            >
              Отправить
            </button>
          )}
        </div>

        <button
          type="button"
          className="forgotPasswordButton"
          onClick={() => {
            setLoginFormLoginPage(true);
            setLoginLoginFormForgetPassword(false);
          }}
        >
          Войти
        </button>
        <button
          type="button"
          className="registrationButton"
        >
          Регистрация
        </button>
      </form>
    );
  }
}

export default ForgetPassword;
