import React from 'react';
import { createStore } from 'redux';

const Actions = {
  INCREASE: 'increase',
  DECREASE: 'decrease',
};

const initialState = 1;

function reducerCounter(state = initialState, action) {
  switch (action.type) {
    case Actions.INCREASE:
      return state + 1;

    case Actions.DECREASE:
      return state - 1;

    default:
      return initialState;
  }
}

// eslint-disable-next-line no-unused-vars
const Counter = ({ counter, change }) => (
  <div className="counter-box">
    <button
      type="button"
    >
      +
    </button>
    <div>
      {initialState}
    </div>
    <button
      type="button"
    >
      -
    </button>
  </div>
);

// eslint-disable-next-line no-unused-vars
const storeCounter = createStore(reducerCounter);

export default Counter;
