import axios from 'axios';
import serverUrl from '../../../../common/constants/urls';

const catalogImage = '/category-image';

async function getCatalogImage(email, updateData) {
  try {
    const response = await axios.get(`${serverUrl}${catalogImage}`);
    const result = response.data;

    const photo = result.filter((item) => item.email === email).map((item) => item.photo);
    updateData(photo);
    return result;
  } catch (error) {
    return null;
  }
}

export default getCatalogImage;
