import { React, useState } from 'react';
import { connect } from 'react-redux';
import Header from '../../main/header/components/HeaderComponent';
import Footer from '../../main/footer/components/FooterComponent';
import ConnectedAdminPersonalAccount from './AdminPersonalAccountComponent';
import postUsersRoles from '../api/post/postUsersRoles';

const PersonalAccount = ({ pages }) => {
  const [isLoaded, setLoading] = useState(false);
  const [errorMessage, setError] = useState('');

  async function handleOnLoad() {
    const adminPage = document.getElementById('adminPage');
    const result = await postUsersRoles(pages.loginPersonalAccountReducer.clientEmail, setLoading, setError);
    result === 'admin' ? adminPage.setAttribute('class', 'test') : adminPage.setAttribute('class', 'test -disabled');
  }
  handleOnLoad();
  return (
    <>
      <Header linkItem={<button type="button" className="buttonBack">Назад</button>} link="/main-page" disabled={false} />
      <div id="adminPage" className="test -disabled">
        <ConnectedAdminPersonalAccount />
      </div>
      <Footer />
    </>
  );
};
const ConnectedPersonalAccount = connect(
  (state) => ({
    pages: state,
  }),
)(PersonalAccount);

export default ConnectedPersonalAccount;
