import setClassErrorById from '../../../common/untils/setClassErrorById';
import postNewProduct from '../api/post/postNewProduct';

const handleButtonClick = async (productName, image, categoryName, productPrice, productInStock,
  categoriesArray, history, isProductsUpdated, setIsProductsUpdated) => {
  const result = await postNewProduct(
    productName, image, categoryName, productPrice, productInStock, categoriesArray,
  );
  if (result === true) {
    history.push('/edit-products');
  } if (result === false) {
    setClassErrorById('errorNewImage', 'errorNewImage');
  }

  isProductsUpdated
    ? setIsProductsUpdated(false)
    : setIsProductsUpdated(true);
};

export default handleButtonClick;
