import React from 'react';
import personalAccount from '../../../assets/personal-account/personal-account.jpg';

class ImageUpload extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      imagePreviewUrl: '',
    };
  }

  handleSubmit(e) {
    this.e.preventDefault();
  }

  handleImageChange(e) {
    e.preventDefault();

    const reader = new FileReader();
    const file = e.target.files[0];

    if (file) {
      reader.onloadend = () => {
        this.setState({
          imagePreviewUrl: reader.result,
        });
      };

      reader.readAsDataURL(file);
    }
  }

  render() {
    const { imagePreviewUrl } = this.state;
    let $imagePreview = null;
    if (imagePreviewUrl) {
      $imagePreview = (<img src={imagePreviewUrl} />);
    } else {
      $imagePreview = (
        <img
          src={personalAccount}
          title="photoPersonalAccount"
          alt="photoPersonalAccount"
          className="photoPersonalAccount"
        />
      );
    }
    console.log(imagePreviewUrl);
    return (
      <div className="previewComponent">
        <form onSubmit={(e) => this.handleSubmit(e)} id="upload-container">
          {$imagePreview}
          <div className="imgPreview">
            <label htmlFor="file-input">
              <p className="choosePhoto">Выберите фото</p>
              <input
                id="file-input"
                name="file"
                className="fileInput"
                type="file"
                onChange={(e) => this.handleImageChange(e)}
                multiple
              />
            </label>
          </div>
        </form>
      </div>
    );
  }
}

export default ImageUpload;
