export const CatalogItemsActions = {
  ADD: 'add',
  DELETE: 'delete',
  COUNTER: 'increaseCounter'
};

export const ADD = {
  type: CatalogItemsActions.ADD,
  item: {
    id: 0,
    description: '',
    img: '',
    price: 0,
    counter: 1,
  },
};

export const COUNTER = {
  type: CatalogItemsActions.COUNTER,
  item: {
    counter: 1,
  },
};

export const DELETE = {
  type: CatalogItemsActions.DELETE,
  itemId: 0,
};
