function checkOnlyNumbers(value) {
  let newValue = '';
  if (value.slice(-1) === '.') {
    newValue = value.slice(0, -5);
  }
  if (value !== value.replace(/[^\d;]/g, '')) {
    return false;
  }
  return value;
}

export default checkOnlyNumbers;
