    let number = '';
    for (const string of this.value) {
      if ('0123456789'.includes(string)) {
        number += string;
      }
    }

    number = parseInt(number);

    if (number < minValue) {
      number = minValue;
    }

    if (number > maxValue) {
      number = maxValue;
    }

    number = formatterNumb.format(number);

    this.value = number;
  });

  inputValue.addEventListener('blur', function (event) {
    let number = '';
    for (const string of this.value) {
      if ('0123456789'.includes(string)) {
        number += string;
      }
    }

    number = parseInt(number);

    this.value = formatter.format(number);
  });

  rangeValue.addEventListener('input', function (event) {
    inputValue.value = formatter.format(parseInt(this.value));
  });
}

export default formatPhoneNumber;
