import React from 'react';
import { Link } from 'react-router-dom';
import setErrorNotNull from '../../untils/setErrorNotNull';
/* import removeErrorNotNull from '../../untils/removeErrorNotNull';
import ErrorSymbol from '../../errorSymbol/components/ErrorSymbolComponent';
import postLoginForgetPassword from '../../api/post/postLoginForgetPasswordRequest'; */
import checkIfUserExists from '../utils/checkIfUserExists';
import InputWitchCkeckingNotNull from '../../input/components/InputWitchCkeckingNotNullComponent';

class ForgetPassword extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      emailInput: 'emailInput',
      emailSymbol: 'errorSymbol',
    };
  }

  render() {
    const {
      onEnter, onLogin, onEnterEmail, pages, history,
    } = this.props;
    const { emailInput, emailSymbol } = this.state;
    async function handleButtonClick() {
      checkIfUserExists()
    }
    return (
      <form className="formForgetPassword">
        <p id="userNotFoundChangePassword" className="userNotFoundBlock -disabled">Пользователя с таким email не существует</p>
        <div className="login">
          <p className="loginString -required">Email:</p>
          <InputWitchCkeckingNotNull
            initialValue={pages.loginPersonalAccountReducer.clientEmail}
            type="email"
            name="emailInput"
            classInput={emailInput}
            classSymbol={emailSymbol}
            updateData=""
            removeErrorLengthFunc=""
            classerrorLength=""
            onEnterEmail={onEnterEmail}
          />
        </div>
        <div
          type="button"
          className=""
        >
          {pages.loginPersonalAccountReducer.clientEmail ? (
            <button
              type="button"
              id="entranceButtonNotNull"
              className="entranceButton"
              onClick={handleButtonClick}
            >
              Отправить
            </button>
          ) : (
            <button
              type="button"
              id="entranceButtonNull"
              className="entranceButton"
              onClick={() => setErrorNotNull(emailInput, emailSymbol)}
            >
              Отправить
            </button>
          )}
        </div>

        <button
          type="button"
          className="forgotPasswordButton"
          onClick={() => {
            onEnter(true, false);
            onLogin(true, true, false);
          }}
        >
          Войти
        </button>
        <Link to="/registration">
          <button
            type="button"
            className="registrationButton"
            onClick={() => onLogin(false, false, false)}
          >
            Регистрация
          </button>
        </Link>
      </form>
    );
  }
}

export default ForgetPassword;
