import React from 'react';

class LoginFormLoginPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      clientLogin: '',
      clientPassword: '',
      loginPageIsVisible: true,
      forgetPasswordIsVisible: false,
    };
  }

  render() {
    const {
      clientLogin, clientPassword, loginPageIsVisible,
    } = this.state;

    const {
      hideLoginPage, showForgetPasswordPage, forgetPasswordIsVisible,
    } = this.props;

    return (
      <>
        {loginPageIsVisible ? (
          <form className="form">
            <div className="login">
              <p className="loginString">Логин:</p>
              <input
                className="loginInput"
                type="text"
                name="LOGIN"
                value={clientLogin}
                onChange={((event) => this.setState({ clientLogin: event.target.value }))}
              />
            </div>
            <div className="password">
              <p className="passwordString">Пароль:</p>
              <input
                className="passwordInput"
                type="password"
                name="PASSWORD"
                value={clientPassword}
                onChange={((event) => this.setState({ clientPassword: event.target.value }))}
              />
            </div>
            <button
              type="button"
              className="entranceButton"
            >
              Войти
            </button>
            <button
              type="button"
              className="forgotPasswordButton"
              onClick={() => {
                this.setState({
                  loginPageIsVisible: false,
                  forgetPasswordIsVisible: true,
                });
                showForgetPasswordPage();
              }}
            >
              Забыли пароль?
            </button>
            <button
              type="button"
              className="registrationButton"
            >
              Регистрация
            </button>
          </form>
        ) : null}
        {forgetPasswordIsVisible
          ? (
            <form className="form">
              <div className="login">
                <p className="loginString">Email:</p>
                <input
                  className="loginInput"
                  type="text"
                  name="LOGIN"
                  value={clientEmail}
                  onChange={((event) => this.setState({ clientEmail: event.target.value }))}
                />
              </div>
              <button
                type="button"
                className="entranceButton"
              >
                Отправить
              </button>
              <button
                type="button"
                className="forgotPasswordButton"
              >
                Войти
              </button>
              <button
                type="button"
                className="registrationButton"
              >
                Регистрация
              </button>
            </form>
          )
          : null}
      </>
    );
  }
}

export default LoginFormLoginPage;
