/* eslint-disable prefer-destructuring */
import React, { useRef, useState } from 'react';
import axios from 'axios';

function FileUpload() {
  const [file, setFile] = useState(''); // storing the uploaded file    // storing the recived file from backend
  const [data, getFile] = useState({ name: '', path: '' }); const [progress, setProgess] = useState(0); // progess bar
  const el = useRef(); // accesing input element
  const handleChange = (e) => {
    setProgess(0);
    // eslint-disable-next-line no-const-assign
    file = e.target.files[0]; // accesing file
    console.log(file);
    setFile(file); // storing file
  };
  const uploadFile = () => {
    const formData = new FormData(); formData.append('file', file); // appending file
    axios.post('http://localhost:4500/upload', formData, {
      onUploadProgress: (ProgressEvent) => {
        progress = `${Math.round(
          ProgressEvent.loaded / ProgressEvent.total * 100,
        )}%`;
        setProgess(progress);
      },
    }).then((res) => {
      console.log(res);
      getFile({
        name: res.data.name,
        path: `http://localhost:4500${res.data.path}`,
      });
    }).catch((err) => console.log(err));
  };
  return (
    <div>
      <div className="file-upload">
        <input type="file" ref={el} onChange={handleChange} />
        {' '}
        <div className="progessBar" style={{ width: progress }}>
          {progress}
        </div>
        <button onClick={uploadFile} className="upbutton" type="button">
          {' '}
          Upload
        </button>
        <hr />
        {/* displaying received image */}
        {data.path && <img src={data.path} alt={data.name} />}
      </div>
    </div>
  );
}
export default FileUpload;
