import getRequest from '../../../../common/api/get/getRequest';

const productsPath = '/products';

async function getProducts(isMounted, isCategory, setState, updateData, setError) {
  try {
    const response = await getRequest(productsPath);
    const result = response.data;
    updateData(isMounted, isCategory, setState, { products: result }, true);
    return result;
  } catch (error) {
    return setError({ errorMessage: error.message });
  }
}

export default getProducts;
