const handleCategoryProductsChange = (e, updateCategoryName, updateData, categoriesArray) => {
  e.preventDefault();
  updateCategoryName(this.setState, e);
  updateData(e.target.value, 'updateProductCategory', categoriesArray.categories);
};

export default handleCategoryProductsChange;
