function formatPhoneNumber(value) {
  let phone = '';

  value.split('').map((letter, index) => {
    if (index === 4) {
      phone += `(${letter}`;
      console.log(index);
    } else if (index === 6) {
      phone += `${letter})`;
    } else if ('+0123456789'.includes(letter)) {
      phone += letter;
      console.log(index);
    }
  });
  return phone;
}

export default formatPhoneNumber;
