import React from 'react';

class LoginForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      clientLogin: '',
      clientPassword: '',
    };
  }

  render() {
    const {
      setLoginForm,
    } = this.props;
    const {
      clientLogin, clientPassword,
    } = this.state;
    const root = document.getElementById('root');
    root.addEventListener('click', (e) => {
      setLoginForm(false);
    });
    return (
    /*       <div
        className="loginForm-box"
        onClick={() => {
          setLoginForm(false);
        }}
        role="presentation"
      > */
      <div>
              <div
        className="loginForm"
        onClick={(e) => {
          e.stopPropagation();
          e.preventDefault();
        }}
        role="presentation"
      >
        <h2>Личный кабинет</h2>
        <div>
          <svg className="backSymbol" viewBox="0 0 20 20">
            <g>
              <path d="M17.89,0H2.11A2.11,2.11,0,0,0,0,2.11V17.89A2.11,2.11,0,0,0,2.11,20H17.89A2.11,2.11,0,0,0,20,17.89V2.11A2.11,2.11,0,0,0,17.89,0ZM19,17.89A1.11,1.11,0,0,1,17.89,19H2.11A1.11,1.11,0,0,1,1,17.89V2.11A1.11,1.11,0,0,1,2.11,1H17.89A1.11,1.11,0,0,1,19,2.11V17.89Z" />
              <path d="M13,7a.5.5,0,0,0-.71,0L10,9.29,7.68,7A.5.5,0,0,0,7,7.68L9.29,10,7,12.32a.5.5,0,0,0,.71.71L10,10.71,12.32,13a.5.5,0,0,0,.71-.71L10.71,10,13,7.68A.5.5,0,0,0,13,7Z" />
            </g>
          </svg>
        </div>
        <form className="form">
          <div className="login">
            <p className="loginString">Логин:</p>
            <input
              className="loginInput"
              type="text"
              name="LOGIN"
              value={clientLogin}
              onChange={(event) => this.setState({
                clientLogin: event.target.value,
              })}
            />
          </div>
          <div className="password">
            <p className="passwordString">Пароль:</p>
            <input
              className="passwordInput"
              type="password"
              name="PASSWORD"
              value={clientPassword}
              onChange={(event) => this.setState({
                clientPassword: event.target.value,
              })}
            />
          </div>
          {/*         <button
              onClick={() => {
                setLoginForm(false);
              }}
              type="button"
              className="entrance"
            >
              Войти
            </button> */}
        </form>
      </div>
      </div>

    );
  }
}

export default LoginForm;
