import React from 'react';

function formatPhoneNumber(value) {
  let phone = [];

  value.split('').forEach((letter, index) => {
    if ('+0123456789'.includes(letter)) {
      if (index === 4) {
        phone.push(`(${letter}`);
      } else if (index === 5) {
        phone += `${letter})`;
      } else {
        phone += letter;
      }
    }
  });
  return phone;
}

class InputComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = { phone: '+375(__)___-__-__' };
  }

  render() {
    const {
      className, name, type, minLength, maxLength, placeholder,
    } = this.props;
    const { phone } = this.state;

    return (
      <>
        <input
          value={phone}
          onFocus={() => this.setState({ phone: '+375' })}
          onChange={(event) => this.setState({ phone: formatPhoneNumber(event.target.value) })}
          className={className}
          name={name}
          type={type}
          minLength={minLength}
          maxLength={maxLength}
          placeholder={placeholder}
        />
      </>
    );
  }
}

export default InputComponent;
