import { React, useState } from 'react';
import { Link } from 'react-router-dom';
import LoginForm from '../../../../common/loginForm/components/LoginFormComponent';
import logo from '../../../../assets/header/logo.png';
import basket from '../../../../assets/common/basket.png';

const Header = ({
  linkItem, link, disabled,
}) => {
  const [loginFormVisible, setLoginFromVisible] = useState(false);
  const divRef = React.createRef();

  return (
    <div className="header-wrap">
      <div className="header-box">
        <div className="logo">
          <img src={logo} alt="Все для дома" title="Все для дома" />
        </div>
        <div className={!disabled ? 'basket' : 'basket -disabled'}>
          <Link to="/basket">
            <img src={basket} alt="Корзина" title="Корзина" />
          </Link>
        </div>
        <Link to={link}>
          {linkItem}
        </Link>
        <div ref={divRef} className="loginDiv">
          <button
            className="buttonLogin"
            type="button"
            onClick={() => {

            }}
          >
            Вход / регистрация
          </button>
        </div>
        <div>
          <LoginForm
   /*          divRef={divRef} */
            loginFormVisible={loginFormVisible}
            setLoginFromVisible={setLoginFromVisible}
          />
        </div>
      </div>

    </div>
  );
};
export default Header;
