import React from 'react';
import InputEditProductsCategory from './InputEditProductsCategoryComponent';

const CategoryColumnEditProducts = ({
  isEditActive, product, isEditActiveId, categoriesArray, updateProductCategory,
}) => (
  <div className="columnCategory">
    {isEditActive && product.id === isEditActiveId
      ? (
        categoriesArray.map((category) => (
          product.categoryId === category.id
                      && (
                        <InputEditProductsCategory
                          key={product.id}
                          category={category.category}
                          categoryId={product.categoryId}
                          updateProductCategory={updateProductCategory}
                        />
                      )
        ))
      ) : categoriesArray.map((category) => (
        product.categoryId === category.id && (
        <p className="test" key={product.id}>
          { category.category }
        </p>
        )
      ))}
  </div>
);

export default CategoryColumnEditProducts;
