/* eslint-disable no-undef */
const initialState = {
  items: [{
    id: 0,
    desciption: 'Светильник потолочный Box Silver',
  }],
  ids: [],
};

const actions = {
  ADD_ITEM: 'add',
  DELETE_ITEM: 'delete',
};

export default (state = initialState, action) => {
  switch (action.type) {
    case actions.ADD_ITEM:
      return {
        items: [
          ...state.items,
          action.payload,
        ],
      };

    default:
      return state;
  }
};
