const processUpdateValueOnChange = (e, updateInput, updateData, categoriesArray = '') => {
  e.preventDefault();
  updateInput(e);
  updateData(e.target.value, 'updateProductCategory', categoriesArray.categories);
};

export default processUpdateValueOnChange;
