/* eslint-disable no-undef */
const initialState = {
  items: [{
    id: 0,
    desciption: 'Светильник потолочный Box Silver',
  }],
  ids: [],
};

store.dispatch({
  type: 'ADD_ITEM',
  text: 'Read the docs',
});

export default (state = initialState, action) => {
  switch (action.type) {
    case ADD_ITEM:
      return {
        items: [
          ...state.items,
          action.payload,
        ],
      };

    default:
      return state;
  }
};
